    margin: "2px 0",
    padding: "4px 8px",
    borderRadius: "4px",
    cursor: "pointer",
    "&:hover": {
      backgroundColor: "rgba(0, 0, 0, 0.05)",
    },
  } as SxProps<Theme>,
};

export const SideBarItemCSS: React.FC<SidebarItemProps> = ({
  item,
  isOpen,
}) => {
  const pathname = usePathname();

