  const hasActiveChild = item.children?.some((child) => pathname === child.href) || false;

  // è™•ç†ä¸»é …ç›®é»æ“Š
  const handleMainItemClick = (e: React.MouseEvent) => {
    if (item.children && item.children.length > 0) {
