      {item.children && item.children.length > 0 && (
        <Box
          sx={{
            ...styles.childrenWrapper,
            gridTemplateRows: isOpen && isExpanded ? "1fr" : "0fr",
            transitionDelay: isOpen ? "0.1s" : "0s",
          }}
        >
          <Box sx={styles.childrenInner}>
            <Stack
              direction="row"
