                    <Link key={index} href={child.href} style={styles.link}>
                      <Stack sx={isChildActive ? styles.activeChildItem : styles.childItem}>
                        <Typography variant="body2" sx={styles.childItemText}>
                          {child.label}
                        </Typography>
                      </Stack>
                    </Link>
                  );
                })}
              </Stack>
            </Stack>
          </Box>
        </Box>
      )}
    </>
  );
};

