  setExpandedByRoute: (itemId: string) => void;
  setExpandedByArrow: (itemId: string) => void;
}

const SideBarContext = createContext<SideBarContextType | undefined>(undefined);

export const useSideBar = () => {
  const context = useContext(SideBarContext);
  if (!context) {
    throw new Error("useSideBar must be used within a SideBarProvider");
  }
  return context;
};

interface SideBarProviderProps {
  children: React.ReactNode;
  sideBarItems?: Array<{
    id: string;
    href: string;
    children?: Array<{
      id: string;
      href: string;
    }>;
  }>;
}

export const SideBarProvider: React.FC<SideBarProviderProps> = ({ 
  children, 
  sideBarItems = [] 
}) => {
  const [isOpen, setIsOpen] = useState(true);
  const [expandedItems, setExpandedItems] = useState<string[]>([]);
  const [manuallyExpandedItems, setManuallyExpandedItems] = useState<string[]>([]);
  const pathname = usePathname();

  // ç›£è½è·¯ç”±è®ŠåŒ–
  useEffect(() => {
    if (!sideBarItems.length) return;

    // æ‰¾å‡ºç•¶å‰è·¯ç”±å°æ‡‰çš„çˆ¶é …ç›®
    const activeParent = sideBarItems.find(item => {
      // æª¢æŸ¥æ˜¯å¦æ˜¯çˆ¶é …ç›®æœ¬èº«
      if (item.href === pathname) return true;
      // æª¢æŸ¥æ˜¯å¦æ˜¯å­é …ç›®
      return item.children?.some(child => child.href === pathname);
    });

    if (activeParent) {
      // è·¯ç”±è®ŠåŒ–æ™‚è‡ªå‹•å±•é–‹å°æ‡‰é …ç›®ï¼Œä½†ä¿ç•™æ‰‹å‹•å±•é–‹çš„é …ç›®
      setExpandedItems(prev => {
        const newExpanded = [activeParent.id];
        // ä¿ç•™æ‰‹å‹•å±•é–‹çš„é …ç›®
        manuallyExpandedItems.forEach(manualId => {
          if (!newExpanded.includes(manualId)) {
            newExpanded.push(manualId);
          }
        });
        return newExpanded;
      });
    }
  }, [pathname, sideBarItems, manuallyExpandedItems]);

  const toggleSideBar = useCallback(() => {
    setIsOpen((prev) => !prev);
  }, []);

  const toggleExpanded = useCallback((itemId: string) => {
    setExpandedItems((prev) => {
      if (prev.includes(itemId)) {
        return prev.filter((id) => id !== itemId);
      } else {
        return [...prev, itemId];
      }
    });
  }, []);

  const expandItem = useCallback((itemId: string) => {
    setExpandedItems((prev) => {
      if (!prev.includes(itemId)) {
        return [...prev, itemId];
      }
      return prev;
    });
  }, []);

  const collapseAllExcept = useCallback((itemId: string) => {
    // ä¿ç•™æ‰‹å‹•å±•é–‹çš„é …ç›®
    setExpandedItems((prev) => {
      const newExpanded = [itemId];
      manuallyExpandedItems.forEach(manualId => {
        if (manualId !== itemId && !newExpanded.includes(manualId)) {
          newExpanded.push(manualId);
        }
      });
      return newExpanded;
    });
  }, [manuallyExpandedItems]);

  // è·¯ç”±è§¸ç™¼çš„å±•é–‹ï¼ˆæœƒæ”¶èµ·å…¶ä»–éæ‰‹å‹•å±•é–‹çš„é …ç›®ï¼‰
  const setExpandedByRoute = useCallback((itemId: string) => {
    setExpandedItems((prev) => {
      const newExpanded = [itemId];
      // ä¿ç•™æ‰‹å‹•å±•é–‹çš„é …ç›®
      manuallyExpandedItems.forEach(manualId => {
        if (manualId !== itemId && !newExpanded.includes(manualId)) {
          newExpanded.push(manualId);
        }
      });
      return newExpanded;
    });
  }, [manuallyExpandedItems]);

  // ç®­é ­è§¸ç™¼çš„å±•é–‹/æ”¶åˆï¼ˆè¨˜éŒ„ç‚ºæ‰‹å‹•å±•é–‹ï¼‰
  const setExpandedByArrow = useCallback((itemId: string) => {
    // æª¢æŸ¥ç•¶å‰æ˜¯å¦å±•é–‹
    const isCurrentlyExpanded = expandedItems.includes(itemId);

    if (isCurrentlyExpanded) {
      // å¦‚æœç•¶å‰æ˜¯å±•é–‹çš„ï¼Œå‰‡æ”¶åˆ
      setExpandedItems(prev => prev.filter(id => id !== itemId));
      // åŒæ™‚å¾æ‰‹å‹•å±•é–‹åˆ—è¡¨ä¸­ç§»é™¤
      setManuallyExpandedItems(prev => prev.filter(id => id !== itemId));
    } else {
      // å¦‚æœç•¶å‰æ˜¯æ”¶åˆçš„ï¼Œå‰‡å±•é–‹
      setExpandedItems(prev => [...prev, itemId]);
      // åŒæ™‚åŠ å…¥æ‰‹å‹•å±•é–‹åˆ—è¡¨
      setManuallyExpandedItems(prev => [...prev, itemId]);
    }
  }, [expandedItems]);

  const value = {
    isOpen,
    expandedItems,
    manuallyExpandedItems,
    toggleSideBar,
    toggleExpanded,
    expandItem,
    collapseAllExcept,
    setExpandedByRoute,
    setExpandedByArrow,
  };

  return (
    <SideBarContext.Provider value={value}>
      {children}
    </SideBarContext.Provider>
  );
};

