    margin: "4px 0",
    padding: "0 8px",
    opacity: isOpen ? 1 : 0,
    visibility: isOpen ? "visible" : "hidden",
    transform: isOpen ? "translateY(0)" : "translateY(-10px)",
    transition: isOpen 
      ? "opacity 0.3s ease 0.35s, transform 0.3s ease 0.35s, visibility 0s 0s"
      : "opacity 0.2s ease, transform 0.2s ease, visibility 0s 0.2s",
  }),
  childItem: {
    margin: "2px 0",
    padding: "4px 8px",
    borderRadius: "4px",
    cursor: "pointer",
    "&:hover": {
      backgroundColor: "rgba(0, 0, 0, 0.05)",
    },
  } as SxProps<Theme>,
};

export const SideBarItem: React.FC<SidebarItemProps> = ({ item, isOpen }) => {
  const pathname = usePathname();

  const isActive = pathname === item.href;
  const hasActiveChild =
    item.children?.some((child) => pathname === child.href) || false;
  const isCurrentSection = isActive || hasActiveChild;

  return (
    <>
      <Link href={item.href} style={styles.link}>
        <Stack direction="row" sx={styles.itemRow}>
          {item.icon}
          <Stack direction="row" sx={styles.itemContent(isOpen)}>
            <Typography>{item.label}</Typography>
            {item.children && item.children.length > 0 && (
              <KeyboardArrowUpIcon sx={styles.arrowIcon(isCurrentSection)} />
            )}
          </Stack>
        </Stack>
      </Link>

      {item.children &&
        item.children.length > 0 &&
        isCurrentSection && (
          <Stack direction="row" sx={styles.childrenContainer(isOpen)}>
            <Box
              sx={{
                width: "24px",
                display: "flex",
                justifyContent: "center",
              }}
            >
              <Divider orientation="vertical" />
            </Box>

            <Stack sx={{ width: "100%" }}>
              {item.children.map((child, index) => (
                <Link key={index} href={child.href} style={styles.link}>
                  <Stack sx={styles.childItem}>
                    <Typography variant="body2" sx={{ fontSize: "0.85rem" }}>
                      {child.label}
                    </Typography>
                  </Stack>
                </Link>
              ))}
            </Stack>
          </Stack>
        )}
    </>
  );
};
